sysauto_script.condition:
//Return 'true' to run the job
var answer = false;
//Get the day of week. 1=Monday, 7=Sunday
var now = new GlideDateTime();
//Run only on weekdays
if(now.getDayOfWeek() < 6){
	answer = true;
}
answer;

sysauto_script.script:
var gr = new GlideRecord('change_request');
gr.addEncodedQuery('state=-5^priority=1');
gr.addQuery('sys_updated_on','<',gs.daysAgoStart(6));
gr.query();
while(gr.next())
	{
	var count = gr.getValue('u_count');   // counter field created with default value 0
	if(count % 7 ==0)   // since the email is to be sent after every 7 days
		{
		gs.eventQueueScheduled('change_request.inactivity',gr,gs.getUserID(),gs.getUserName());
		gr.autoSysFields(false);
		gr.setWorkflow(false);
		gr.update();
	}
	count++;   // counter will be updated each time the job is run which is daily
	gr.setValue('u_count',count); // capture the value of the updated counter
	gr.autoSysFields(false);
	gr.setWorkflow(false);
	gr.update();
}
